import React, { Component } from 'react';
import {Mutation} from 'react-apollo'
import gql from 'graphql-tag'
import Form from './styles/Form'
import formatMoney from '../lib/formatMoney'

class CreateItem extends Component {
    state = {
        title: '',
        description: '',
        image: '',
        largeImage: '',
        price: 0,
    }
    handleTitleChange = (e)=>{
        this.setState({title: e.target.value})
    }

    render() {
        return (
            <Form>
                <h2>Sell an item</h2>
                <fieldset>
                    <label htmlFor="title">
                        Title
                        <input type="text" id="title" name="title" placeholder="title" value={this.state.price} onChange={this.handleTitleChange} required />
                    </label>
                </fieldset>
            </Form>
        );
    }
}

export default CreateItem;
