import React, { Component } from 'react';
import {Mutation} from 'react-apollo'
import gql from "graphql-tag";
import Form from './styles/Form'
import Error from "./ErrorMessage";

class Signup extends Component {

    state = {
        email: '',
        password: '',
        name: ''
    }

    saveToState = (e) => {
        const {name, type, value} = e.target
        this.setState({[name]: value})
    }
    render() {
        return (
            <Form>
                <fieldset>
                    <h2>Sign up for an account</h2>
                    <label htmlFor='email'/>
                    Email
                    <input type='text' name='email' placeholder='email' value={this.state.email} onChange='saveToState' />

                    <label htmlFor='name'/>
                    Name
                    <input type='text' name='name' placeholder='name' value={this.state.name} onChange='saveToState' />

                    <label htmlFor='password'/>
                    Password
                    <input type='text' name='password' placeholder='email' value={this.state.email} onChange='saveToState' />
                </fieldset>
            </Form>
        );
    }
}

export default Signup;
