import React, { Component } from 'react';
import {Mutation, Query} from 'react-apollo'
import gql from 'graphql-tag'
import Form from './styles/Form'
import formatMoney from '../lib/formatMoney'
import Error from './ErrorMessage'
import Router from 'next/router'

const SINGLE_ITEM_QUERY = gql`
    query SINGLE_ITEM_QUERY($id: ID!){
        item(where: {id: $id}){
            id
            title
            description
            price
        }
    }
`
const UPDATE_ITEM_MUTATION = gql`
    mutation UPDATE_ITEM_MUTATION(
        $title: String!,
        $description: String!,
        $price: Int!,
        $image: String,
        $largeImage: String
    ){
        createItem(
            title: $title,
            description: $description,
            price: $price,
            image: $image,
            largeImage: $largeImage){
            id
        }
    }
`  

class UpdateItem extends Component {
    state = {}
    handleChange = (e)=>{
        const {name, type, value} = e.target
        const val = type === 'number' ? parseInt(value) : value
        this.setState({[name]: val})
    }

   
    render() {
        return (
            <Query query={SINGLE_ITEM_QUERY} variables={{id: this.props.id}}>
                {({data, loading})=>{
                    if(loading){return <p>Loading...</p>}
                    return (
                    <Mutation mutation={UPDATE_ITEM_MUTATION} variables={this.state}>
                        {(createItem, {loading, error})=>(
                        <Form onSubmit={async e=>{
                            e.preventDefault()
                            const res = await createItem();
                            Router.push({
                                pathname: '/items',
                                query: {id: res.data.createItem.id}
                            })
                            }}>
                            <h2>Sell an item</h2>
                            <fieldset disabled={loading} aria-busy={loading}>
                                <label htmlFor="title">
                                    Title
                                    <input type="text" id="title" name="title" placeholder="title" defaultValue={data.item.title} onChange={this.handleChange} required />
                                </label>
                                <label htmlFor="title">
                                    Description
                                    <textarea type="text" id="description" name="description" placeholder="description" value={data.item.description} onChange={this.handleChange} required />
                                </label>
                                <label htmlFor="title">
                                    Price
                                    <input type="number" id="price" name="price" placeholder="title" value={data.item.price} onChange={this.handleChange} required />
                                </label>
                                <button type="submit">Submit</button>
                                <Error error={error} />
                            </fieldset>
                        </Form>
                        )}
                    </Mutation>
                )}}
            </Query>
        );
    }
}

export default UpdateItem;
export { UPDATE_ITEM_MUTATION }
