import React, { Component } from 'react';
import {Mutation} from 'react-apollo'
import gql from 'graphql-tag'
import Form from './styles/Form'
import formatMoney from '../lib/formatMoney'

class CreateItem extends Component {
    state = {
        title: '',
        description: '',
        image: '',
        largeImage: '',
        price: 0,
    }
    handleChange = (e)=>{
        const {name, type, value} = e.target
        const val = type === 'number' ? parseInt(value) : value
        this.setState({[name]: val})
    }

    render() {
        return (
            <Form>
                <h2>Sell an item</h2>
                <fieldset>
                    <label htmlFor="title">
                        Title
                        <input type="text" id="title" name="title" placeholder="title" value={this.state.title} onChange={this.handleChange} required />
                    </label>
                    <label htmlFor="title">
                        Price
                        <input type="text" id="description" name="description" placeholder="description" value={this.state.description} onChange={this.handleChange} required />
                    </label>
                    <label htmlFor="title">
                        Description
                        <input type="number" id="price" name="price" placeholder="title" value={this.state.price} onChange={this.handleChange} required />
                    </label>
                </fieldset>
            </Form>
        );
    }
}

export default CreateItem;
