    render() {
        return (
            
            <Mutation mutation={DELETE_ITEM_MUTATION} variables={{id: this.props.id}} update={this.update}>
                {(deleteItem, { error }) => (
                    <button onClick={() => {
                        if (confirm('Are you sure want to delete this item?')) {
